(139, 59, 'Shortest path of the king', '2016-04-06 10:12:28', 'Accpted', '// ACMTraining.cpp : Defines the entry point for the console application.\r\n//\r\n#include "stdafx.h"\r\n#include <iostream>\r\n#include <math.h>\r\n#include <string>\r\nusing namespace std;\r\n\r\n\r\nstruct Position\r\n{\r\n	short PositionI;\r\n	short PositionJ;\r\n};\r\n\r\nPosition GetPosition(string postion)\r\n{\r\n	Position src;\r\n	switch (postion[0])\r\n	{\r\n	case ''a'':\r\n		src.PositionI = 0;\r\n		break;\r\n	case ''b'':\r\n		src.PositionI = 1;\r\n		break;\r\n	case ''c'':\r\n		src.PositionI = 2;\r\n		break;\r\n	case ''d'':\r\n		src.PositionI = 3;\r\n		break;\r\n	case ''e'':\r\n		src.PositionI = 4;\r\n		break;\r\n	case ''f'':\r\n		src.PositionI = 5;\r\n		break;\r\n	case ''g'':\r\n		src.PositionI = 6;\r\n		break;\r\n	case ''h'':\r\n		src.PositionI = 7;\r\n		break;\r\n	default:\r\n		src.PositionI = -1; // invalid param\r\n		break;\r\n	}\r\n	src.PositionJ = (postion[1] - ''0'') - 1; // ''1'' = 49, ''0'' = 48 --> ''7'' - ''0'' = 7\r\n	return src;\r\n}\r\n\r\nvoid PrintPath(Position src, Position dist)\r\n{\r\n	short horizontal = src.PositionI - dist.PositionI; // get the horizontal steps negative for left positive for right\r\n	short vertical = src.PositionJ - dist.PositionJ; // get the vertical steps negative for down positive for up\r\n	if(horizontal < 0 && vertical < 0) // RU and/or up and or right\r\n	{\r\n		if(horizontal < vertical)\r\n			cout << -horizontal <<endl;\r\n		else\r\n			cout << -vertical <<endl;\r\n		while (horizontal != 0 && vertical != 0)\r\n		{\r\n			cout<< "RU"<<endl;\r\n			horizontal++;\r\n			vertical++;\r\n		}\r\n		while (horizontal < 0) // to finish up the rest if there value\r\n		{\r\n			cout<< "R"<<endl;\r\n			horizontal++;\r\n		}\r\n		while (vertical < 0) // to finish up the rest if there value\r\n		{\r\n			cout<< "U"<<endl;\r\n			vertical++;\r\n		}\r\n	}\r\n	else if(horizontal > 0 && vertical > 0) // LD and/or D and or L\r\n	{\r\n		if(horizontal > vertical)\r